import React, {Component} from 'react';
import './App.css';
import Header from './Components/Header';
import Sidebar from './Components/Sidebar';
import Maincontent from './Components/Maincontent';
import Footer from './Components/Footer';

 export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
       colorList:[],
       currentColor:[]
    }
    this.generateRandomColor();
  }


generateRandomColor = () => {
  let result =[];
  let  counter = 1;
      let obj ={
               id:null,
                name:''
               }
    for(let i= 0; i<=7; i++) {
     let array1 =[];

     for(let j= 0;j<12; j++) {
        let data ='#'+(Math.random()*0xFFFFFF<<0).toString(16);
        obj.id  = counter;
        obj.name = data;
        array1.push(obj);
        counter++;
        obj = {
        id:null,
          name:''
      }
  }
  result.push(array1)
 }
 this.state.colorList = result;
}
onButtonClick = (ind) => {
   return this.setState({currentColor:this.state.colorList.filter((color,index) => {
         if(ind == index) {
          return color
         }
   })})
   console.log(this.state.currentColor)
} 

render() {
  console.log("hello", this.state)
  return (
    <div className="App">
     <Header />
     <div className="container-fluid">
      <div className="row">
      <div className="col-md-3">
      <Sidebar />
      </div>
      <div className="col-md-9">
      <Maincontent  />
      <Footer  footerColor ={this.state.colorList} onButtonClick = {this.onButtonClick} />
      </div>

    </div>
     </div>


    </div>
  );
}
 }

export default App;
