export class ColorsComponent extends Component {
    constructor(props) {
        super(props);
    }


    render() {
        const { name } = this.props.color;
        return (
            <div  className ="main_color" style={mainColorDiv} style ={{backgroundColor: name, padding: '30%' }} onClick={this.props.colorSelect.bind(this, this.props.color)}>
                <div>
                  <p>{name}</p>
                </div>

            </div>
        )
    }
}
