import React, { Component } from 'react'
import ListOfAddedColor from './ListOfAddedColor';
import DiffrentComponent from './DiffrentComponent';
import '../styles/colorComponent.css';

export class SelectedColor extends Component {
       constructor(props) {
           super(props);
           this.state = {
               title:''
           }
       }
       onChangeInput =(e) => this.setState({[e.target.name]:e.target.value});

        onSubmit = (e) =>{
          e.preventDefault();
           this.props.SelectedColor(this.state.title);
           this.setState({title:''});
        }


    render() {
    return (
        <div className="selecedColor">
         <div  className="listOfColor" >
         <h2></h2>
         <ListOfAddedColor listOFColor ={this.props.selectColor} onDeleteColor={this.props.onDeleteColor.bind(this)} ></ListOfAddedColor>
         </div>
          <form onSubmit = {this.onSubmit} > 
          <div className ="form-group-inline search_form">
           <label htmlFor="name"></label>
           <input  className="form-control" type="text" name="title" value={this.state.value} onChange ={this.onChangeInput.bind(this)}></input>
           <button type="submit" style={indexButton} className="btn btn-primary" >Save New Color</button>
           </div>
          </form>

        <hr />
       <div>
           <DiffrentComponent colorPlatter = {this.props.formState} onDeleteCurrentColor={this.props.onDeleteCurrentColor} ></DiffrentComponent>

       </div>
        </div>





    )





    }
}
const indexButton = {
    width: '200px',
    height: '40px',
    border: '1px solid  transperant',
    display: 'inline-block',
    backgrounColor: 'darkblue',
    borderRadius: '28px',
    margin: '0 auto'
}



export default SelectedColor
